class Documento extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Documento_model');
    } 

    /*
     * Listing of documento
     */
    function index()
    {
        $data['all_documento'] = $this->Documento_model->get_all_documento();
        
        $data['_view'] = 'documento/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new documento
     */
    function add()
    {   
        $this->load->library('form_validation');
        $this->form_validation->set_rules('nombre_doc','Nombre','trim|required', array('required' => 'Este Campo no debe ser vacio'));
        if($this->form_validation->run())     
        {
            $params = array(
                'nombre_doc' => $this->input->post('nombre_doc'),
            );
            $id_doc = $this->Documento_model->add_documento($params);
            redirect('documento/index');
        }else{            
            $data['_view'] = 'documento/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a documento
     */
    function edit($id_doc)
    {   
        // check if the Diametrored exists before trying to edit it
        $data['documento'] = $this->Documento_model->get_documento($id_doc);
        
        if(isset($data['documento']['id_doc']))
        {
            $this->load->library('form_validation');
            $this->form_validation->set_rules('nombre_doc','Nombre','trim|required', array('required' => 'Este Campo no debe ser vacio'));
            if($this->form_validation->run())     
            {
                $params = array(
                    'nombre_doc' => $this->input->post('nombre_doc'),
                );
                
                $this->Documento_model->update_documento($id_doc,$params);            
                redirect('documento/index');
            }else{
                $data['_view'] = 'documento/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The documento you are trying to edit does not exist.');
    }
    
