class Usuario extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Usuario_model');
    } 

    /*
     * Listing of usuario
     */
    function index()
    {
        $data['usuario'] = $this->Usuario_model->get_all_usuario();
        
        $data['_view'] = 'usuario/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new usuario
     */
    function add()
    {   
        $this->load->library('form_validation');

		$this->form_validation->set_rules('login_usu','Login Usu','required');
		$this->form_validation->set_rules('clave_usu','Clave Usu','required');
		
		if($this->form_validation->run())     
        {   
            $params = array(
				'tipo_usuario' => $this->input->post('tipo_usuario'),
				'terminal_usu' => $this->input->post('terminal_usu'),
				'estado_usu' => $this->input->post('estado_usu'),
				'nombre_usu' => $this->input->post('nombre_usu'),
				'login_usu' => $this->input->post('login_usu'),
				'clave_usu' => $this->input->post('clave_usu'),
				'imagen_usu' => $this->input->post('imagen_usu'),
				'fechahora_usu' => $this->input->post('fechahora_usu'),
            );
            
            $usuario_id = $this->Usuario_model->add_usuario($params);
            redirect('usuario/index');
        }
        else
        {            
            $data['_view'] = 'usuario/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a usuario
     */
    function edit($id_usu)
    {   
        // check if the usuario exists before trying to edit it
        $data['usuario'] = $this->Usuario_model->get_usuario($id_usu);
        
        if(isset($data['usuario']['id_usu']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('login_usu','Login Usu','required');
			$this->form_validation->set_rules('clave_usu','Clave Usu','required');
		
			if($this->form_validation->run())     
            {   
                $params = array(
					'tipo_usuario' => $this->input->post('tipo_usuario'),
					'terminal_usu' => $this->input->post('terminal_usu'),
					'estado_usu' => $this->input->post('estado_usu'),
					'nombre_usu' => $this->input->post('nombre_usu'),
					'login_usu' => $this->input->post('login_usu'),
					'clave_usu' => $this->input->post('clave_usu'),
					'imagen_usu' => $this->input->post('imagen_usu'),
					'fechahora_usu' => $this->input->post('fechahora_usu'),
                );

                $this->Usuario_model->update_usuario($id_usu,$params);            
                redirect('usuario/index');
            }
            else
            {
                $data['_view'] = 'usuario/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The usuario you are trying to edit does not exist.');
    } 

    /*
     * Deleting usuario
     */
    function remove($id_usu)
    {
        $usuario = $this->Usuario_model->get_usuario($id_usu);

        // check if the usuario exists before trying to delete it
        if(isset($usuario['id_usu']))
        {
            $this->Usuario_model->delete_usuario($id_usu);
            redirect('usuario/index');
        }
        else
            show_error('The usuario you are trying to delete does not exist.');
    }
    
}
