class Asociado_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get asociado by id_asoc
     */
    function get_asociado($id_asoc)
    {
        $asociado = $this->db->query("
            SELECT
                *
            FROM
                `asociado`
            WHERE
                `id_asoc` = ?
        ",array($id_asoc))->row_array();
        return $asociado;
    }

    function get_pendientes($id_asoc)
    {
        $pendientes = $this->db->query("
             SELECT
                f.* , l.id_asoc, l.mes_lec, l.gestion_lec, l.id_lec, l.consumo_lec
            FROM
                factura f, lectura l 
            
            
            WHERE f.id_lec=l.id_lec
            and f.estado_fact='PENDIENTE'
            and l.id_asoc=".$id_asoc."

        ")->result_array();

        return $pendientes;
    }
    function get_canceladas($id_asoc)
    {
        $cancelados = $this->db->query("
            SELECT
                f.* , l.id_asoc, l.mes_lec, l.gestion_lec, l.consumo_lec
            FROM
                factura f, lectura l
            WHERE f.id_lec=l.id_lec
            and f.estado_fact='CANCELADA'
            and l.id_asoc=".$id_asoc."
            ORDER BY f.id_lec ASC

        ")->result_array();

        return $cancelados;
    }
        
    /*
     * Get all asociado
     */
    function get_all_asociado()
    {
        $asociado = $this->db->query("
            SELECT
                a.*
            FROM
                asociado a
            WHERE
                1 = 1
            ORDER By a.apellidos_asoc, a.nombres_asoc
        ")->result_array();

        return $asociado;
    }
        
    /*
     * function to add new asociado
     */
    function add_asociado($params)
    {
        $this->db->insert('asociado',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update asociado
     */
    function update_asociado($id_asoc,$params)
    {
        $this->db->where('id_asoc',$id_asoc);
        return $this->db->update('asociado',$params);
    }
    
    /*
     * function to delete asociado
     */
    function delete_asociado($id_asoc)
    {
        return $this->db->delete('asociado',array('id_asoc'=>$id_asoc));
    }
    /* busca asociados relacionados con un parametro dado. */
    function get_busqueda_asociado_parametro($parametro, $servicioestado)
    {
        $sql = "SELECT
                        a.*, l.mes_lec, l.gestion_lec, l.actual_lec, l.fecha_lec
                FROM
                        asociado a
                LEFT JOIN lectura l on a.id_asoc = l.id_asoc
                WHERE
                     (a.nombres_asoc like '%".$parametro."%' or a.apellidos_asoc like '%".$parametro."%'
                      or a.ci_asoc like '%".$parametro."%' or a.direccion_asoc like '%".$parametro."%'
                      or a.codigo_asoc ='".$parametro."' or a.telefono_asoc like '%".$parametro."%'
                      or a.nit_asoc like '%".$parametro."%' or a.razon_asoc like '%".$parametro."%'
                      or a.zona_asoc like '%".$parametro."%' or a.medidor_asoc like '%".$parametro."%'
                      or a.servicios_asoc like '%".$parametro."%' or a.categoria_asoc like '%".$parametro."%')
                      ".$servicioestado."
                GROUP by a.id_asoc
                ORDER By a.apellidos_asoc, a.nombres_asoc";
        $asociado = $this->db->query($sql)->result_array();
        return $asociado;

    }
    /* Get all asociado and lectura inicial*/
    function get_all_asociado_lecturainicial()
    {
        $asociado = $this->db->query("
            SELECT
                a.*, l.mes_lec, l.gestion_lec, l.actual_lec, l.fecha_lec
            FROM
                asociado a
            LEFT JOIN lectura l on a.id_asoc = l.id_asoc
            WHERE
                1 = 1
            GROUP by a.id_asoc
            ORDER By a.apellidos_asoc, a.nombres_asoc
        ")->result_array();

        return $asociado;
    }
    /*
     * Get all asociado modificado en una fecha determinada
     */
    function get_all_asociado_modificado($fecha1, $fecha2)
    {
        $asociado = $this->db->query("
            SELECT
                a.*
            FROM
                asociado a
            WHERE
                date(a.fechahora_asoc) >= '".$fecha1."'
                and date(a.fechahora_asoc) <= '".$fecha2."'
            /*ORDER By a.apellidos_asoc, a.nombres_asoc*/
        ")->result_array();

        return $asociado;
    }
    /* devuelve al asociado segun usuario y contraseÃ±a */
    function get_esteasociado($login, $clave)
    {
        $asociado = $this->db->query("
            SELECT *
            from asociado WHERE login_asoc = '".$login."'".
                    " and clave_asoc = '".md5($clave)."' ".
                    " and estado = 'ACTIVO'
        ")->row();
        return $asociado;
    }
    /* retorna lecturas de una gestion, esto para que lo vea el asociado */
    function getall_lecturasasociado($asociado_id, $gestion_lec)
    {
        $asociado = $this->db->query("
            SELECT l.*, a.nombres_asoc, a.apellidos_asoc, a.tipo_asoc, a.categoria_asoc 
            from lectura l, asociado a
            where a.id_asoc=l.id_asoc and
                  l.gestion_lec = '".$gestion_lec."' and
                  l.id_asoc=".$asociado_id."
            order by l.fecha_lec DESC
        ")->result_array();

        return $asociado;
    }
}
