    /*
     * Get expedido by ciudad
     */
    function get_expedido($ciudad)
    {
        $expedido = $this->db->query("
            SELECT
                *

            FROM
                `expedido`

            WHERE
                `ciudad` = ?
        ",array($ciudad))->row_array();

        return $expedido;
    }
        
    /*
     * Get all expedido
     */
    function get_all_expedido()
    {
        $expedido = $this->db->query("
            SELECT
                *

            FROM
                `expedido`

            WHERE
                1 = 1

            ORDER BY `ciudad` DESC
        ")->result_array();

        return $expedido;
    }
        
    /*
     * function to add new expedido
     */
    function add_expedido($params)
    {
        $this->db->insert('expedido',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update expedido
     */
    function update_expedido($ciudad,$params)
    {
        $this->db->where('ciudad',$ciudad);
        return $this->db->update('expedido',$params);
    }
    
    /*
     * function to delete expedido
     */
    function delete_expedido($ciudad)
    {
        return $this->db->delete('expedido',array('ciudad'=>$ciudad));
    }
