    /*
     * Get factura by id_fact
     */
    function get_factura($id_fact)
    {
        $factura = $this->db->query("
            SELECT
                *

            FROM
                `factura`

            WHERE
                `id_fact` = ?
        ",array($id_fact))->row_array();

        return $factura;
    }
        
    /*
     * Get all factura
     */
    function get_all_factura()
    {
        $factura = $this->db->query("
            SELECT
                *

            FROM
                `factura`

            WHERE
                1 = 1

            ORDER BY `id_fact` DESC
        ")->result_array();

        return $factura;
    }
        
