    function buscar_asociado($ci)
    {

        $sql = "select * from asociado where nit_asoc = ".$ci." or ci_asoc=".$ci." or codigo_asoc=".$ci;        
        $resultado = $this->db->query($sql)->result_array();
        
        return $resultado;
    }
