    function get_pendiente_factura($asociado)
    {

        $sql = "SELECT f.*, l.*
        FROM factura f, lectura l
        WHERE 
        l.id_lec=f.id_lec 
        and f.estado_fact='PENDIENTE'
        and l.id_asoc=".$asociado." ";        
        $resultado = $this->db->query($sql)->result_array();
        
        return $resultado;
    }

