    function recargosadetalle($id_param,$factura_id)
    {

        $sql = "INSERT INTO detalle_factura(
        id_fact,
        cant_detfact,
        descip_detfact,
        punit_detfact,
        desc_detfact,
        total_detfact,
        tipo_detfact) 
        values(
        SELECT
        ".$factura_id.",
        1,
        detalle_param,
