class Mes_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get mes by id_mes
     */
    function get_mes($id_mes)
    {
        $mes = $this->db->query("
            SELECT
                *

            FROM
                `mes`

            WHERE
                `id_mes` = ?
        ",array($id_mes))->row_array();

        return $mes;
    }
        
    /*
     * Get all mes
     */
    function get_all_mes()
    {
        $mes = $this->db->query("
            SELECT * FROM mes 
            ORDER BY id_mes
        ")->result_array();

        return $mes;
    }
        
    /*
     * function to add new mes
     */
    function add_mes($params)
    {
        $this->db->insert('mes',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update mes
     */
    function update_mes($id_mes,$params)
    {
        $this->db->where('id_mes',$id_mes);
        return $this->db->update('mes',$params);
    }
    
    /*
     * function to delete mes
     */
    function delete_mes($id_mes)
    {
        return $this->db->delete('mes',array('id_mes'=>$id_mes));
    }
}
