    {
        parent::__construct();
    }
    
    /*
     * Get tipo_aporte by tipo
     */
    function get_tipo_aporte($tipo)
    {
        $tipo_aporte = $this->db->query("
            SELECT
                *

            FROM
                `tipo_aporte`

            WHERE
                `tipo` = ?
        ",array($tipo))->row_array();

        return $tipo_aporte;
    }
        
    /*
     * Get all tipo_aporte
     */
    function get_all_tipo_aporte()
    {
        $tipo_aporte = $this->db->query("
            SELECT
                *

            FROM
                `tipo_aporte`

            WHERE
                1 = 1

            ORDER BY `tipo` DESC
        ")->result_array();

        return $tipo_aporte;
    }
        
    /*
     * function to add new tipo_aporte
     */
    function add_tipo_aporte($params)
    {
        $this->db->insert('tipo_aporte',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update tipo_aporte
     */
    function update_tipo_aporte($tipo,$params)
    {
        $this->db->where('tipo',$tipo);
        return $this->db->update('tipo_aporte',$params);
    }
    
    /*
     * function to delete tipo_aporte
     */
    function delete_tipo_aporte($tipo)
    {
        return $this->db->delete('tipo_aporte',array('tipo'=>$tipo));
    }
