class Usuario_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get usuario by id_usu
     */
    function get_usuario($id_usu)
    {
        $usuario = $this->db->query("
            SELECT
                *

            FROM
                `usuario`

            WHERE
                `id_usu` = ?
        ",array($id_usu))->row_array();

        return $usuario;
    }
        
    /*
     * Get all usuario
     */
    function get_all_usuario()
    {
        $usuario = $this->db->query("
            SELECT
                *

            FROM
                `usuario`

            WHERE
                1 = 1

            ORDER BY `id_usu` 
        ")->result_array();

        return $usuario;
    }

     public function getCurrentPassword($id_usu)
    {
       $query = $this->db->where('id_usu',$id_usu)
                        ->get('usuario');
            if ($query->num_rows() > 0) {
                return $query->row();
            }
    }
    public function password($id_usu, $new_password)
    {
            $data = array(
                    'clave_usu'=> $new_password
            );
            $this->db->where('id_usu',$id_usu);
             return $this->db->update('usuario',$data);
    }
        
    /*
     * function to add new usuario
     */
    function add_usuario($params)
    {
        $this->db->insert('usuario',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update usuario
     */
    function update_usuario($id_usu,$params)
    {
        $this->db->where('id_usu',$id_usu);
        return $this->db->update('usuario',$params);
    }
    
    /*
     * function to delete usuario
     */
    function delete_usuario($id_usu)
    {
        return $this->db->delete('usuario',array('id_usu'=>$id_usu));
    }

    function inactivar_usuario($id_usu)
    {
        $sql = "update usuario set estado_usu = 'INACTIVO' where id_usu = ".$id_usu;
        
        return $this->db->query($sql);
    }
    function activar_usuario($id_usu)
    {
        $sql = "update usuario set estado_usu = 'ACTIVO' where id_usu = ".$id_usu;
        
        return $this->db->query($sql);
    }
    
    function get_all_usuario_activo()
    {
        $usuario = $this->db->query("
            SELECT
                u.*

            FROM
                usuario u

            WHERE
                u.estado_usu = 'ACTIVO'

            ORDER BY u.nombre_usu
        ")->result_array();

        return $usuario;
    }
}
