    /*
     * Get zona by zona_med
     */
    function get_zona($zona_med)
    {
        $zona = $this->db->query("
            SELECT
                *

            FROM
                `zonas`

            WHERE
                `zona_med` = ?
        ",array($zona_med))->row_array();

        return $zona;
    }
        
    /*
     * Get all zonas
     */
    function get_all_zonas()
    {
        $zonas = $this->db->query("
            SELECT
                *

            FROM
                `zonas`

            WHERE
                1 = 1

            ORDER BY `zona_med` DESC
        ")->result_array();

        return $zonas;
    }
        
