    }
    
    /*
     * function to update zona
     */
    function update_zona($zona_med,$params)
    {
        $this->db->where('zona_med',$zona_med);
        return $this->db->update('zonas',$params);
    }
    
    /*
     * function to delete zona
     */
    function delete_zona($zona_med)
    {
        return $this->db->delete('zonas',array('zona_med'=>$zona_med));
